import React, { useEffect, useState } from "react";
import axios from "axios";

const Home = () => {
  const [movies, setMovies] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const result = await axios.get(
          `https://www.omdbapi.com/?s=${searchQuery || "movie"}&apikey=1b457a18`
        );
        setMovies(result.data.Search); // Result from OMDB has "Search" array
        setLoading(false);
      } catch (error) {
        setError("Failed to fetch movies.");
        setLoading(false);
      }
    };

    fetchMovies();
  }, [searchQuery]); // Fetch new movies when searchQuery changes

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="home">
      <h2>Trending Movies</h2>
      <input
        type="text"
        value={searchQuery}
        onChange={handleSearchChange}
        placeholder="Search for movies"
      />
      <div className="movie-grid">
        {movies &&
          movies.map((movie) => (
            <div key={movie.imdbID} className="movie-card">
              <img
                src={movie.Poster !== "N/A" ? movie.Poster : "https://via.placeholder.com/500"}
                alt={movie.Title}
              />
              <div className="movie-info">
                <h3>{movie.Title}</h3>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Home;
