import React, { useEffect, useState } from "react";
import axios from "axios";

const Home = () => {
  const [movies, setMovies] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [watchlist, setWatchlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const result = await axios.get(
          `https://www.omdbapi.com/?s=${searchQuery || "movie"}&apikey=1b457a18`
        );
        setMovies(result.data.Search); // Result from OMDB has "Search" array
        setLoading(false);
      } catch (error) {
        setError("Failed to fetch movies.");
        setLoading(false);
      }
    };

    fetchMovies();
  }, [searchQuery]); // Fetch new movies when searchQuery changes

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleAddToWatchlist = (movie) => {
    const updatedWatchlist = [...watchlist, movie];
    setWatchlist(updatedWatchlist);
    localStorage.setItem("watchlist", JSON.stringify(updatedWatchlist)); // Save to local storage
  };

  const handleRemoveFromWatchlist = (movieId) => {
    const updatedWatchlist = watchlist.filter((movie) => movie.imdbID !== movieId);
    setWatchlist(updatedWatchlist);
    localStorage.setItem("watchlist", JSON.stringify(updatedWatchlist)); // Save to local storage
  };

  useEffect(() => {
    const storedWatchlist = JSON.parse(localStorage.getItem("watchlist"));
    if (storedWatchlist) {
      setWatchlist(storedWatchlist); // Load watchlist from local storage
    }
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="home">
      <h2>Trending Movies</h2>
      <input
        type="text"
        value={searchQuery}
        onChange={handleSearchChange}
        placeholder="Search for movies"
      />
      <div className="movie-grid">
        {movies &&
          movies.map((movie) => (
            <div key={movie.imdbID} className="movie-card">
              <img
                src={movie.Poster !== "N/A" ? movie.Poster : "https://via.placeholder.com/500"}
                alt={movie.Title}
              />
              <div className="movie-info">
                <h3>{movie.Title}</h3>
                <button onClick={() => handleAddToWatchlist(movie)}>Add to Watchlist</button>
                {watchlist.some((item) => item.imdbID === movie.imdbID) && (
                  <button onClick={() => handleRemoveFromWatchlist(movie.imdbID)}>
                    Remove from Watchlist
                  </button>
                )}
              </div>
            </div>
          ))}
      </div>

      <h2>Watchlist</h2>
      <div className="movie-grid">
        {watchlist.length === 0 && <div>Your watchlist is empty.</div>}
        {watchlist &&
          watchlist.map((movie) => (
            <div key={movie.imdbID} className="movie-card">
              <img
                src={movie.Poster !== "N/A" ? movie.Poster : "https://via.placeholder.com/500"}
                alt={movie.Title}
              />
              <div className="movie-info">
                <h3>{movie.Title}</h3>
                <button onClick={() => handleRemoveFromWatchlist(movie.imdbID)}>
                  Remove from Watchlist
                </button>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Home;
