import React, { useEffect, useState } from "react";
import axios from "axios";

const Home = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const result = await axios.get(
          "https://www.omdbapi.com/?s=movie&apikey=1b457a18"
        );
        setMovies(result.data.Search); // OMDB API returns movies in 'Search' field
        setLoading(false);
      } catch (error) {
        console.error(error); // Log the error details to the console
        setError("Failed to fetch movies.");
        setLoading(false);
      }
    };
    fetchMovies();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="home">
      <h2>Trending Movies</h2>
      <div className="movie-grid">
        {movies.length > 0 ? (
          movies.map((movie) => (
            <div key={movie.imdbID} className="movie-card">
              <img
                src={movie.Poster !== "N/A" ? movie.Poster : "https://via.placeholder.com/500"}
                alt={movie.Title}
              />
              <div className="movie-info">
                <h3>{movie.Title}</h3>
              </div>
            </div>
          ))
        ) : (
          <p>No movies found.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
