import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MovieDetails = ({ match }) => {
  const [movieDetails, setMovieDetails] = useState(null);
  const { imdbID } = match.params;  

  useEffect(() => {
    const fetchMovieDetails = async () => {
      const result = await axios.get(
        `https://www.omdbapi.com/?i=${imdbID}&apikey=${process.env.REACT_APP_OMDB_API_KEY}`
      );
      setMovieDetails(result.data);
    };
    fetchMovieDetails();
  }, [imdbID]);

  return (
    <div className="movie-details">
      {movieDetails ? (
        <>
          <h2>{movieDetails.Title}</h2>
          <img src={movieDetails.Poster} alt={movieDetails.Title} />
          <p>{movieDetails.Plot}</p>
          <ul>
            <li><strong>Year:</strong> {movieDetails.Year}</li>
            <li><strong>Rated:</strong> {movieDetails.Rated}</li>
            <li><strong>Genre:</strong> {movieDetails.Genre}</li>
            <li><strong>Director:</strong> {movieDetails.Director}</li>
            <li><strong>Actors:</strong> {movieDetails.Actors}</li>
          </ul>
        </>
      ) : (
        <p>Loading movie details...</p>
      )}
    </div>
  );
};

export default MovieDetails;
